}

function populate_user(group_id){
    var obj = {};
    if(isEmpty(users)) return obj;

    for(var counter in users){
        if(users[counter].data){
            if(group_id in users[counter].data.groups){
                obj[users[counter]._id] = {    
                    user_id   : users[counter]._id,
                    firstname : users[counter].firstname ? users[counter].firstname : "",
                    lastname  : users[counter].lastname ? users[counter].lastname : "",
                    email     : users[counter].email ? users[counter].email : "",
                    device_id : users[counter].device_id ? users[counter].device_id : "",
                    phone     : users[counter].virtual_phone  
                };
            }
        }
    }
    return obj;
}

function checkPastDeliveryDue(due_date){
    var current_date = new Date().getTime();
    //return true;
    //return (due_date > new Date()) ; //120000 is default which is 120 sec or 2 minutes 
    return current_date > due_date ; //confirms the past_due records not the future one records
}

function checkFutureDeliveryDue(due_date){
    var current_data = new Date().getTime();
    return (due_date > current_data);
}

function populate_records(list){
    var details = [], users = [];
    if(isEmpty(list)) return {};

    for(var itr in list){
