function populate_compnay_task_id(task_arr){
    if(isEmpty(task_arr)) return null;

    for(var itr in task_arr){
        if( ("company_bot_detail" in task_arr[itr].type) ){
            return task_arr[itr]._id;
        }
    }
    return null;
} 

function set_task_id(task_obj, company_task_id){
    if(isEmpty(company_task_id)) {
        return [task_obj._id];
    }else{
        if(("company_bot_detail" in task_arr[itr].type) ){
            return [task_obj._id];
        }else{
            return [task_obj._id, company_task_id];
        }
    }
}

