function process_details_with_timeout(details){
    var updated_details = [];
    if(isEmpty(details)) return updated_details;

    for(var itr in details){
        if( !isEmpty(details[itr].timeout) && (details[itr].processed === 0) ){
            var newDetailObj = JSON.parse(JSON.stringify(details[itr]));

            //check If added detail delivery_due_date is a past date or not ?
            if(checkPastDeliveryDue(details[itr].due_date)){

                if("company_bot_detail" in details[itr].type){
                    var company_timeout = JSON.parse(JSON.stringify(newDetailObj.timeout)),
                        temp_obj        = JSON.parse(JSON.stringify(company_timeout));

                    for(var loop in company_timeout){
                        var ctimeoutObj     = company_timeout[loop];
                        var cgroupUsers     = populate_group_users(ctimeoutObj.delivery_user_types);

                        temp_obj[loop].delivery_user_ids        = populate_delivery_ids(cgroupUsers);
                        temp_obj[loop].delivery_user_special    = cgroupUsers ;
                        temp_obj[loop].message_id               = details[itr]._id;
                        temp_obj[loop].from_user_id             = details[itr].user_incoming.note;
                    }
                    newDetailObj.processed      = 1;
                    newDetailObj.status         = 2; //2 refres to complete
                    newDetailObj.category       = "message";
                    newDetailObj.user_calculate = temp_obj;
                    newDetailObj.timeout        = {};
                    updated_details.push(newDetailObj);
                }else{

                    var timeoutObj   = details[itr].timeout,
                        groupUsers   = populate_group_users(timeoutObj.delivery_user_types);

                    newDetailObj.processed                      = 1;
                    newDetailObj.status                         = 2;
                    newDetailObj.category                       = "message";
                    newDetailObj.timeout.delivery_user_ids      = populate_delivery_ids(groupUsers) ;
                    newDetailObj.timeout.delivery_user_special  = groupUsers;
                    newDetailObj.timeout.message_id             = details[itr]._id;
                    newDetailObj.timeout.from_user_id           = details[itr].user_incoming.note;

                    newDetailObj.user_calculate = newDetailObj.timeout;
                    newDetailObj.timeout        = {};
                    updated_details.push(newDetailObj);
                }
            }

            if(checkFutureDeliveryDue(details[itr].due_date)){
                if(newDetailObj.status === 0) {
                    newDetailObj.status = 1;
                    updated_details.push(newDetailObj);
                }
            }
        }
    }

    return updated_details;
}

