function populate_tasks(list, task_name){
    if(isEmpty(list)) return [];
    if(isEmpty(task_name)) return [];
    var tasks_ids = [];
    for(var itr in list){
        if(list[itr].value.table == "task_table"){
            if(list[itr].value.task_name.toString() == task_name.toString()){
                tasks_ids.push(list[itr].value._id);
            }
        }
    }

    return tasks_ids;
}

function isEmpty(obj){

    if(Object.prototype.toString.call(obj) === "[object Object]"){
        if( Object.keys(obj).length ){
            return false;
        }else{
            return true;
        }
    }else if(Object.prototype.toString.call(obj) === "[object Array]"){
        if( obj.length ){
            return false;
        }else{
            return true;
        }
    }else{
        if( obj ){
            return false;
        }else{
            return true;
        }
    }   
}

