function format_db_records(list){
    var users = [], templates = [], details=[], task_table = [], user_task = [], sorted_detail = [];
    for(var i=0; i<list.length; i++){
        if(list[i].doc.table == "template"){
            templates.push(list[i].doc);
        }
        if(list[i].doc.table == "details"){
            details.push(list[i].doc);
        }

        if(list[i].doc.table == "task_table"){
            task_table.push(list[i].doc);
        }

        if(list[i].doc.table == "users"){
            users.push(list[i].doc);
        }

        if(list[i].doc.table == "user_task"){
            user_task.push(list[i].doc);
        }
    }

    if(!isEmpty(details)){
        sorted_detail =  details.sort(function(a,b){
            sorted_detail = sorted_detail.sort(function(a,b){
            	if(typeof a.page_id === 'number'){
            		return (a.page_id - b.page_id);	
            	}else{
            		return (a.createdAt - b.createdAt);	
            	}
            });
        });
    }else{
        sorted_detail = details ;
    }

    return {users: users, templates: templates, details: sorted_detail, tasks: task_table, user_tasks: user_task} ;
}

